package clientUIUtilities;// Added in Phase 5import client.*;public class ClientInputKeyAdapter extends java.awt.event.KeyAdapter {  ChatClient adaptee;  public ClientInputKeyAdapter(ChatClient adaptee)   {    this.adaptee = adaptee;  }  public void keyPressed(java.awt.event.KeyEvent e)   {    if (e.getKeyCode()==e.VK_ENTER)      adaptee.notifyObservers("#enter");  }}